
function sendContactEmail() {
    $("#contactResultDiv").addClass("hidden");
    $("#contactErrorDiv").addClass("hidden").fadeIn();

    var dataset = {
        NOMBRE: $("#name_contact").val(),
        MAIL: $("#email_contact").val(),
        TELEFONO: $("#celular_contact").val(),
        DISTRITO: $("#distrito_contact").val(),
        MENSAJE: $("#message_contact").val()
    };

    if (dataset.NOMBRE == "") {
        $("#contactErrorDiv").removeClass("hidden").fadeIn();
        return void $("#contactErrorDiv .errors-text").html("Debe ingresar un nombre");
    }
    if (dataset.MAIL == "") {
        $("#contactErrorDiv").removeClass("hidden").fadeIn();
        return void $("#contactErrorDiv .errors-text").html("Debe ingresar un correo");
    }
    
    if (dataset.TELEFONO == "") {
        $("#contactErrorDiv").removeClass("hidden").fadeIn();
        return void $("#contactErrorDiv .errors-text").html("Debe ingresar un nùmero de celular o telefono");
    }
    if (dataset.DISTRITO == "") {
        $("#contactErrorDiv").removeClass("hidden").fadeIn();
        return void $("#contactErrorDiv .errors-text").html("Debe ingresar un distrito");
    }

    var dataContact = {
        MENSAJE: "Nombre: " + $("#name_contact").val() + "\n" +
        "Correo: " + $("#email_contact").val() + "\n" +
        "Mensaje: " + $("#message_contact").val() + "\n" +
        "Telefono: " + $("#celular_contact").val() + "\n" +
        "Distrito: " + $("#distrito_contact").val(),
        TIPOMENSAJE: "TerSup " + $("#name_contact").val(),
        
    }

    var c = $.ajax({
        type: "POST",
        url: "enviocorreo.php",
        data: dataContact
    });
    return c.done(function () {
        $("#contactResultDiv").removeClass("hidden"),
            $("#contactResultDiv").html("El mensaje se envío correctamente"),
            $("#name_contact").val(""),
            $("#email_contact").val(""),
            $("#message_contact").val(""),
            $("#celular_contact").val(""),
            $("#distrito_contact").val("");
    }), c.fail(function (a) {
        $("#contactErrorDiv").removeClass("hidden").fadeIn();
        try {
            var b = JSON.parse(a.responseText);
            if (b.message) return void $("#contactErrorDiv .errors-text").html(b.message);
        } catch (c) {
            $("#contactErrorDiv .errors-text").text("Hubo un problema al enviar el mensaje.");
        }
    }), !1;
}